  "CSSSrc": ".html
.body
{
	width: 1920px;
	height: 1080px;
	margin: 0;
	padding: 0;
}

.slide
{
	width: 100%;
	height: 100%;
	position: absolute;
	left: 0;
	top: 0;
}
.slide.key
{
	background-color: white;
}

.main
{
	position: absolute;
	top: 0;
	left: 0;
	height: 100%;
	width: 100%;
	z-index: 10;
}

.title-box
{
	position: absolute;
	top: 45vh;
	left: 5vw;
	width: 20vw;
	height: 20vh;
	background: transparent;
	display: flex;
	flex-direction: column;
	justify-content: space-between;
}

.content-box
{
	position: absolute;
	top: 0vh;
	left: 30vw;
	width: 70vw;
	height: 100vh;
	background: radial-gradient(circle, rgba(255,255,255,1) 64%, rgba(220,220,220,1) 100%);
	display: flex;
	flex-direction: column;
	justify-content: center;
}

.church-logo
{
	position: absolute;
	left: 50px;
	top: 70px;
	z-index: 10;
	width: 180px;
	height: 180px;
}

.textbox
{
	display: flex;
	flex-direction: row;
	justify-content: space-evenly;
	box-sizing: border-box;
	padding-top: 5px;
	padding-bottom: 5px;
	padding-left: 30px;
	align-items: left;
	width: 100%;
	height: 100%;
}
.textbox.row
{
	flex-direction: row;
}
.textbox.col
{
	flex-direction: column;
}

.text
{
	font-family: Arial;
	font-size: 36pt;
	color: #efefef;
}
.text.bold
{
	font-weight: bold;
}
.text.italic
{
	font-style: italic;
}
.text.center
{
	text-align: center;
}
.text.key
{
	color: white;
}
.text.gray
{
	color: Black;
}


"
}

